import React, { useState } from "react";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([
    { id: "task_1", title: "Learn JS", status: 1 },
    { id: "task_2", title: "Code a Todo List", status: 0 },
    { id: "task_3", title: "Code with VS", status: 1 },
  ]);
  const [showIncomplete, setShowIncomplete] = useState(false);
  const [newTask, setNewTask] = useState("");

  return (
    <div className="container">
      <h1 className="title">
        Todo List
        <span>Get one item done at a time.</span>
      </h1>
      <ul className="task-list">
        {tasks.map((task) => (
          <li key={task.id} className={task.status ? "done" : ""}>
            <span className="label">{task.title}</span>
            <div className="actions">
              <input type="checkbox" className="btn-action btn-action-done" />
              <button className="btn-action btn-action-delete">âœ–</button>
            </div>
          </li>
        ))}
      </ul>
      <div className="filter-wrapper">
        <label htmlFor="filter" className="filter-label">
          Show incompleted task only
        </label>
        <input type="checkbox" id="filter" />
      </div>
      <form action="#" className="form">
        <label htmlFor="newitem">Add to the todo list</label>
        <input type="text" id="newitem" value={newTask} />
        <button type="submit">Add Item</button>
      </form>
    </div>
  );
}
export default App;
