import React, { useState } from "react";
import "./App.css";

const allItems = [
  { id: "beaming", value: "😁", isSmile: true },
  { id: "grinning", value: "😀", isSmile: true },
  { id: "squinnting", value: "😆", isSmile: true },
  { id: "winking", value: "😉", isSmile: true },
  { id: "sad", value: "😥", isSmile: true },
];

function App() {
  const [isLoggedIn, setIsLoggerIn] = useState(false);
  const [items, setItems] = useState(allItems);

  const hanndleLogin = () => {
    setIsLoggerIn(true);
  };

  const hanndleLogout = () => {
    setIsLoggerIn(false);
  };

  const removeItem = (itemId) => {
    setItems(items.filter((item) => item.id !== itemId));
  };

  return (
    <div>
      <h1>Smileys</h1>
      <div className="loginControl">
        {isLoggedIn ? (
          <button onClick={hanndleLogout}>Logout</button>
        ) : (
          <button onClick={hanndleLogin}>Login</button>
        )}
      </div>
      <div>
        {isLoggedIn && (
          <ul>
            {items.map((item) => (
              <li key={item.id}>
                <label htmlFor={`${item.id}-input`}>
                  {item.value} {item.id}
                </label>
                <input
                  id={`${item.id}-input`}
                  type="text"
                  defaultValue={item.value}
                />
                <button onClick={() => removeItem(item.id)}>remove</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
export default App;
