import React, { useState, useEffect } from "react";
import "./App.css";

const api = {
  key: "db55104333ae4ffcec77520a6e1b4058",
  base: "https://api.openweathermap.org/data/2.5/forecast/hourly?q={city name}&appid={API key}",
};

function App() {

  const [searchInput,setSearchInput] = useState("");

  return (
    <>
      <form>
      <input type="text" placeholder="City" value={searchInput} onChange = {(e) => setSearchInput(e.target.value)}/>
      <button>Search</button>
      </form>
    </>
  );
}
export default App;
